INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (140, 'Children''s Center at 42nd Street', '<p>November 2008 marked the return of a circulating children&rsquo;s library to The New York Public Library on Fifth Avenue. The Children&rsquo;s Center at 42nd Street continues NYPL''s tradition as a national leader in children''s services.<!--break--></p>
<p>The collection offers a wealth of circulating materials for children of all ages, and for adults with an interest in children&rsquo;s literature, music, and media. The collection, services, and facilities include:</p>
<ul>
    <li>A well-rounded selection of informational books targeted to children&rsquo;s educational and curricular needs.</li>
    <li>Generous collections of picture book stories for sharing with younger readers, easy storybooks for newly independent readers, and novels for readers up to age twelve.</li>
    <li>Collections of musical CDs, books on CD, video games, and DVDs for children.</li>
    <li>Eight PC stations with internet access, available by appointment and reserved for use by children twelve and under.</li>
    <li>A flat screen cable TV with gaming equipment for special programs.</li>
    <li>Trained professional children&rsquo;s librarians always on hand to help patrons of all ages to navigate NYPL catalogs and other resources.</li>
</ul>
<p>The Children&rsquo;s Center at 42nd Street features on-going public programs for young people and their families. From intimate readings to large-scale events, programming includes storytelling, musical and theatrical performances, spoken word performances, and guest appearances by authors and illustrators. The Children&rsquo;s Center at 42nd Street&nbsp;is a ''go to'' destination for kids from&nbsp;infancy to age 12, as well as their parents and caregivers.</p>', '2126210208', NULL, NULL, 'childrenscenter42@nypl.org', NULL, NULL, '2009-01-06 11:25:21 -0500', '2013-04-16 15:23:58 -0400', NULL, NULL, NULL, NULL);
INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (141, 'Young Adult Collection, Video media, New Books', '<p>Circulation Desk, Reserves, New Books, Young Adult Collection (Fiction, Non-fiction, Graphic Novels, Manga, Exam Prep Books), DVDs. Eight full-use PCs and one Catalog PC. Copy machine is near public bathroom.</p>', '2124776770', NULL, NULL, 'sewardpark@nypl.org', NULL, NULL, '2009-01-06 12:34:11 -0500', '2012-02-13 17:13:57 -0500', NULL, NULL, NULL, NULL);
