INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (143, 'Adult Collection, Reference, Periodicals', '<div><span>Adult Collection (Fiction, Non-fiction, Classics, Mystery, Romance, Science Fiction &amp; Fantasy, Urban), Reference, Books on CD, Large Print, Foreign language books, Audio CDs, Newspapers and magazines</span>. One catalog PC. 8 Internet PCs: 4 for adult patrons; 4 for young adult patrons after 2 p.m. Copy machine is under the wall clock.</div>', '2124776770', NULL, NULL, 'sewardpark@nypl.org', NULL, NULL, '2009-01-06 12:51:17 -0500', '2012-02-13 17:30:10 -0500', NULL, NULL, NULL, NULL);
INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (144, 'Center for Reading and Writing', '<p><span>The New York Public Library&rsquo;s Center for Reading and Writing (CRW) provides basic literacy instruction for adults 16 years of age and older.&nbsp;Instruction is provided in ten week cycles, four times per year.&nbsp;Small groups consist of 4 to 6 students which meet twice per week with a trained tutor.&nbsp;Computer-assisted instruction is an important part of the program and is open to registered students by appointment during center hours.</span></p>
<p>Open Hours:&nbsp;</p>
<p>Tuesday, 1:00 - 6:00 pm</p>
<p>Thursday, 1:00 - 6:00 pm</p>
<p>Friday, 1:00 - 5:00 pm</p>
<p>CRW Phone: <strong>(212)&nbsp;529-2909</strong></p>', '2125292909', NULL, NULL, 'theresasheehan@nypl.org', NULL, NULL, '2009-01-06 12:53:21 -0500', '2012-01-04 17:40:43 -0500', NULL, NULL, NULL, NULL);
INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (145, 'Community Room', '<p>Open to the public for programming only, Community Room for programs, Center for Reading and Writing offices</p>', '2124776770', NULL, NULL, 'seward_park@nypl.org', NULL, NULL, '2009-01-06 12:56:46 -0500', '2012-02-13 17:28:15 -0500', NULL, NULL, NULL, NULL);
INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (146, 'Microforms Reading Room', '<p>The Microforms Section, Room 100 provides access to over 350,000 microfilm reels and 3,000,000 microfiche resources in the humanities and social sciences. Types of materials include microfilm copy of periodical backfiles, collections of antiquarian monographs, historical pamphlets, incunabula, and many unique archives of diverse national and international organizations.</p>
<p>Current issues of unbound periodical titles in 22 languages from 128 countries are also accessible in Room 100. The Microform Reading Room is Wi-Fi enabled.</p>
<!--break-->
<p>These microfilm guides are subject to change; please verify the titles through our <a href="http://catalog.nypl.org/search/">catalog system</a> as well.</p>
<ul>
    <li><a href="http://www.nypl.org/sites/default/files/usa_newspapers_1.62.pdf">U.S. regional and local newspapers [PDF]</a></li>
    <li><a href="/sites/default/files/New_York_City_Newspapers.pdf">New York City newspapers [PDF]</a></li>
    <li><a href="http://www.nypl.org/sites/default/files/international_newspapers_by_country.pdf">International newspapers by COUNTRY [PDF]</a></li>
    <li><a href="/sites/default/files/international_paper_by_title.pdf">International newspapers by TITLE [PDF]</a></li>
    <li><a href="/sites/default/files/EarlyAmericanNewspapers.pdf">Early American newspapers [PDF]</a></li>
</ul>
<p>In addition, the Microforms Reading Room also collect an extensive collection of phone books and (reverse)address directories from across the country:</p>
<p>NEW YORK CITY   &amp; LONG ISLAND: WHITE (Residential) AND YELLOW (Business)PAGES</p>
<ul>
    <li>CURRENT:2005 - Present: most recent white/yellow for NYC boroughs are kept in Rooms 100, <a href="http://www.nypl.org/locations/schwarzman/periodicals-room">108</a> and <a href="http://www.nypl.org/locations/schwarzman/general-research-division/catalog-room">315</a></li>
</ul>
<ul>
    <li><span dir="LTR">BACKFILES: Microfiche is available for the five boroughs for dates ranging from 1976 to 2005. Earlier dates are on microfilm, although for some areas date ranges continue into the 1980s. For most U.S. cities from 1976 to 2005, they are on MICROFICHE. All U.S. telephone directories end in 2005.</span></li>
