INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (181, 'Manuscripts, Archives and Rare Books Division', '<p>The Manuscripts, Archives and Rare Books Division collects, preserves and makes available for research purposes rare, unique and primary materials which document the history and culture of peoples of African descent throughout the world, with a concentration on the Americas and the Caribbean.</p>
