INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (193, 'Theatre on Film and Tape Archive', '<p><strong>Researchers must call (212) 870-1642 to make a viewing&nbsp;appointment. TOFT will accept e-mail requests for appointments from researchers outside of the U.S. &nbsp;E-mail: &nbsp;toft@nypl.org.</strong></p>
<p><strong>Please Be Advised: </strong></p>
<p><strong>Limited Walk-ins</strong><br />
Due to class screenings, we will&nbsp;at times&nbsp;be unable to take walk-in appointments.&nbsp;Please call our screening room in advance to confirm availability:&nbsp; (212) 870-1642.</p>
<h5><strong>Videotapes are Play-Only</strong></h5>
<p>To protect our older videotapes and preserve the machines that play them, TOFT must limit playback controls to &quot;Play Only.&quot; &nbsp;Fast-forward, rewind, and pause functions will no longer be permitted for titles on older videotape. Please call the screening room at (212) 870-1642 with any questions.</p>
<p>Since 1970, the Theatre on Film and Tape Archive (TOFT) has preserved live theatrical productions and documented the creative contributions of distinguished artists and legendary figures of the theatre.&nbsp; With the consent and cooperation of the theatrical unions and each production''s artistic collaborators, TOFT produces video recordings of Broadway, Off-Broadway, and regional theatre productions as well as dialogues between notable theatre personalities.</p>
<!--break-->
<p>These&nbsp;recordings have revolutionized theatre research,&nbsp;ensuring that today''s performances will be tomorrow''s legacy, available to future generations for study and inspiration. Endowed, in part, by producer Lucille Lortel, TOFT is housed in a state-of-the-art screening room named in her honor.&nbsp;Titles in the collection may be found in the Library''s online catalog (<a href="http://catalog.nypl.org/search">http://catalog.nypl.org/search</a>) or by emailing or telephoning TOFT.&nbsp;&nbsp;The Archive is available to theatre professionals, students, or researchers with work or study-related reasons for viewing. &nbsp;Advance appointments are strongly recommended for viewing. Please call (212) 870-1642.</p>', '2128701642', NULL, NULL, 'toft@nypl.org', NULL, NULL, '2009-01-08 11:46:20 -0500', '2013-04-17 13:47:44 -0400', NULL, NULL, NULL, NULL);
INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (194, 'Robert W. Wilson Music Circulating Collection Reading Room', '<p>Reference Desk, Books, Scores, CD''s,&nbsp;Song Index</p>', '9172756975', NULL, NULL, NULL, NULL, NULL, '2009-01-08 13:18:16 -0500', '2010-09-15 13:01:35 -0400', NULL, NULL, NULL, NULL);
