INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (385, 'First Floor', '<p>The&nbsp;first floor&nbsp;features special format Adult&nbsp;audiobooks (cassette and digital); reference books in standard print, large print, and braille; periodicals in standard print, large print, and braille; a Young Adult section; and a Children''s Room. There are Closed Circuit TVs to enlarge text, accessible computers for public use, and Kurzweil scanners for converting print to audio. Used large print books are offered for sale in the lobby.</p>', NULL, NULL, NULL, NULL, NULL, NULL, '2009-02-11 18:17:45 -0500', '2010-01-07 18:32:42 -0500', NULL, NULL, NULL, NULL);
