INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (397, 'Adult Books Collection', '<p>A large number of adult books on a wide variety of topics are located on this floor. Periodicals from scholarly journals to current popular magazines are found near the reading room. Internet access for free public use by appointment is available. <br />
Services to Persons with Disabilities&nbsp;<br />
Conference room offers the following assistive technology to the public:</p>
<ul>
    <li>Personal Reading Machine - scans printed text and reads aloud in synthetic speech <br />
    &nbsp;</li>
    <li>Telecommunications devices for the deaf (TTY) are available for borrowing<br />
    &nbsp;</li>
    <li>Closed Circuit Television Enlarger (CCTV) - enlarges printed material 45-60 X&nbsp;<br />
    &nbsp;</li>
    <li>Computer screen magnifying software available (MAGIC) - available&nbsp;on all public computers in the library<br />
    &nbsp;</li>
    <li>Computer screen reading software (JAWS) - available on all public computers in the library</li>
</ul>', NULL, NULL, NULL, NULL, NULL, NULL, '2009-02-11 23:24:20 -0500', '2013-04-20 19:52:04 -0400', NULL, NULL, NULL, NULL);
INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (400, 'Manuscripts and Archives Division', '<p>The Division holds over 29,000 linear feet of archival material in over 3,000 collections. The strengths of the Division are the papers and records of individuals, families, and organizations, primarily from the New York region. These collections support research in the political, economic, social, and cultural history of New York and the United States.&nbsp;</p>
<p>[img_assist|nid=223040|title=archives.nypl.org|desc=|link=url|url=http://archives.nypl.org/|align=center|width=540|height=292]</p>
<p><strong>The new </strong><a href="http://archives.nypl.org/"><strong>archives portal</strong></a><strong> searches over 8,000 collections across the Libary''s Research Centers including the Manuscripts and Archives Division.</strong></p>
<p><strong><u>Class visits<br />
<br />
</u></strong>The Manuscripts and Archives Division welcomes class visits and collaboration with area colleges and universities.&nbsp;To schedule, or discuss class visit opportunities with an archivist, please fill out the <a href="http://www.nypl.org/node/211651">Class Visit Application</a>.&nbsp;</p>
<p><strong><u>Schedule an appointment<br />
<br />
</u></strong>If you expect to research archives or have a question, please use our&nbsp;<a href="http://www.nypl.org/locations/tid/36/node/58098">application form</a>&nbsp;to ask a reference question, or request archival material.&nbsp;</p>
<p>
