INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (403, 'George Arents Collection', '<p>The George Arents Collection on Tobacco is a comprehensive collection on the history, literature, and lore of tobacco. The Arents Collection of Books in Parts is one of the most important collections assembled of serialized publications that remain in their original paper wrappers.</p>
<p>Researchers whose work requires use of George Arents Collection materials must&nbsp;<a target="_blank" href="http://www.nypl.org/locations/tid/36/node/209462">register for access</a>. Registration may be completed either in advance or in person.&nbsp;Please visit&nbsp;<a href="http://www.nypl.org/locations/tid/36/node/54747">Conducting Research: George Arents Collection</a>&nbsp;to learn more.</p>
<p>If you have questions about the collection, please email&nbsp;<a href="mailto:rarebook@nypl.org">rarebook@nypl.org</a>.</p>
<p>&nbsp;</p>', '2126420110', '2123024815', NULL, 'rarebook@nypl.org', NULL, NULL, '2009-02-12 22:40:14 -0500', '2013-05-29 13:26:41 -0400', NULL, NULL, NULL, NULL);
INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (404, 'Rare Book Division', '<p>The Rare Book Division houses approximately 200,000 titles, covering five centuries of printing&mdash;from the 1450s to the present&mdash;and representing Continental Europe, England, and the Americas.&nbsp;</p>
<p>Researchers whose work requires use of Rare Book Division collections must <a target="_blank" href="http://www.nypl.org/locations/tid/36/node/209462">register for access</a>. Registration may be completed either in advance or in person.&nbsp;Please visit <a href="http://www.nypl.org/locations/tid/36/node/29552">Conducting Research: Rare Book Division</a>&nbsp;to learn more.</p>
<p>If you have questions about the collection, please email <a href="mailto:rarebook@nypl.org">rarebook@nypl.org</a>.</p>', '2126420110', '2123024815', NULL, 'rarebook@nypl.org', NULL, NULL, '2009-02-12 22:44:46 -0500', '2013-05-29 13:25:45 -0400', NULL, NULL, NULL, NULL);
INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (405, 'The Miriam and Ira D. Wallach Division of Art, Prints and Photographs: Print Collection', '<p>The most accessible print room in New York City, the Print Collection of The New York Public Library encompasses over 200,000 prints along with a specialized reference collection of over 15,000 volumes on the history of prints and printmakers.  To explore our holdings, please consult our <a href="http://wallachprintsandphotos.nypl.org/">online catalog</a>.</p>

<p>Materials are available through the Prints and Photographs Study Room (308) with a card of admission, during the hours above.&nbsp;In addition, you can follow us on <a href="http://www.facebook.com/pages/Art-Prints-and-Photographs-Collections-of-the-NYPL/213023842053451"><font color="#0000ff">Facebook</font></a>, <a href="http://www.flickr.com/groups/artsprintsphotographsnypl"><font color="#0000ff">Flickr</font></a>&nbsp;and <a href="http://artprintsphotographsnypl.tumblr.com/"><font color="#0000ff">Tumblr</font></a>.</p>
<p><span>If you have questions about the collection, please email&nbsp;</span><span>prnref@nypl.org</span><font size="2"><span>.</span></font></p>', '2129300817', NULL, NULL, 'prnref@nypl.org', NULL, NULL, '2009-02-12 23:22:10 -0500', '2012-04-30 15:07:31 -0400', NULL, NULL, NULL, NULL);
INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (406, 'The Miriam and Ira D. Wallach Division of Art, Prints and Photographs: Photography Collection', '<p>The Photography Collection of the New York Public Library comprises approximately 500,000 photographs, including examples of almost every photographic process from the earliest daguerreotypes to contemporary digital images.  To explore our holdings, please consult our <a href="http://wallachprintsandphotos.nypl.org/">online catalog</a>.</p>

<p>Materials are available through the Prints and Photographs Study Room (308) by appointment only. In addition, you can follow us on <a href="http://www.facebook.com/pages/Art-Prints-and-Photographs-Collections-of-the-NYPL/213023842053451"><font color="#0000ff">Facebook</font></a>, <a href="http://www.flickr.com/groups/artsprintsphotographsnypl"><font color="#0000ff">Flickr</font></a>&nbsp;and <a href="http://artprintsphotographsnypl.tumblr.com/"><font color="#0000ff">Tumblr</font></a>.</p>', '2129300837', '2129300530', NULL, 'phgref@nypl.org', NULL, NULL, '2009-02-12 23:30:20 -0500', '2012-04-30 15:08:59 -0400', NULL, NULL, NULL, NULL);
INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (408, 'The Miriam and Ira D. Wallach Division of Art, Prints and Photographs: Art & Architecture Collection', '<p>The Art and Architecture Collection is the primary access point for readers and researchers using the Research Libraries&rsquo; materials which relate to the fine arts, decoration, art history, architectural history, design and theory. Contact us with your research questions at <a href="mailto:artref@nypl.org"><strong>artref@nypl.org</strong></a>! In addition, you can follow us on <a href="http://www.facebook.com/pages/Art-Prints-and-Photographs-Collections-of-the-NYPL/213023842053451"><strong>Facebook</strong></a>, <a href="http://www.flickr.com/groups/artsprintsphotographsnypl"><strong>Flickr</strong></a>&nbsp;and <a href="http://artprintsphotographsnypl.tumblr.com/"><strong>Tumblr</strong></a>.</p>
<!--break-->
<p>Collected primarily in English and Western European languages, the extensive collections include monographs, exhibition catalogs, auction records, periodicals, monographic series, ephemera, oeuvre and catalogues raisonnes.</p>
<p>The Art and Architecture Collection, located in Room 300, is accessible from the South Hall&nbsp;of the Main Reading Room on the third floor of the Stephen A. Schwarzman Building.</p>', '2129300835', '2129300530', NULL, 'artref@nypl.org', NULL, NULL, '2009-02-12 23:44:05 -0500', '2011-09-16 12:06:35 -0400', NULL, NULL, NULL, NULL);
