INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (484, 'Spencer Collection', '<p>The Spencer Collection surveys the illustrated word and book bindings of all periods and all countries and cultures, from medieval manuscripts, Japanese scrolls, and Indian miniatures to monuments in Renaissance printing, illustration and binding to contemporary livres d''artistes. Materials are available through the Prints and Photographs Study Room (308) by appointment only and with a card of admission, during the hours above.</p>
<!--break-->
<p>Readers wishing to consult the The Library''s Renaissance and Medieval Manuscripts are required to request and receive permission in writing before their research visits. <a href="/locations/tid/36/node/29598">Please see this page for more information</a>.</p>
<p>Although not administratively part of the Miriam and Ira D. Wallach Division of Art, Prints and Photographs, the Spencer Collection materials are available through the Prints and Photographs Study Room (308) with 24 hours notice and a card of admission obtained from the Print Collection. Spencer Collection items catalogued after 1972 can be found in <a href="http://catalog.nypl.org/search/X">The Library''s Online Catalog</a>. For pre-1972 holdings, consult the <em>Dictionary Catalog and Shelf List of the Spencer Collection of Illustrated Books and Manuscripts and Fine Bindings</em>. (Boston: G.K. Hall, 1971), found at the <strong>Print Room Desk</strong> in Room 308 and in the General Research Division, Room 315 with the call number <strong>Pub. Cat. Z1023 .N572 1971</strong></p>
<p>&nbsp;<span style="font-size: 13px; line-height: 17px; ">If you have questions about the collection, please email&nbsp;</span><span style="font-size: 13px; line-height: 17px; ">prnref@nypl.org</span><span style="font-size: 13px; line-height: 17px; ">.</span></p>', '2129300817', '2129300530', NULL, 'prnref@nypl.org', NULL, NULL, '2009-02-18 14:40:37 -0500', '2012-08-15 10:37:04 -0400', NULL, NULL, NULL, NULL);
