INSERT INTO locations ("loc_id", "name", "description", "phone", "fax", "tty", "email", "handicapped_access", "activities", "created", "modified", "amenities", "socialmedia", "floorplan", "division_referral") VALUES (645, 'Job Search Central', '<p>[img_assist|nid=62609|title=|desc=|link=none|align=left|width=300|height=248]The New York Public Library&rsquo;s Job Search Central provides up-to-date information for anyone interested in looking for a job, researching careers, or exploring a career change. This includes materials on different careers, resume writing, job search strategies, and self exploration.</p>
